mapper.select:
SELECT c.* FROM admissionScore a,college c
WHERE c.id=a.collegeId AND a.ensureHigh <= #{ranking} AND a.ensureLow >= #{ranking}
AND c.province=#{localProvince} AND a.science=#{science}
ORDER BY a.ensureLow-#{ranking} DESC
LIMIT #{limitNum}

SELECT c.* FROM admissionScore a,college c
WHERE c.id=a.collegeId AND a.stablityHigh <= #{ranking} AND a.stablityLow >= #{ranking}
AND c.province=#{localProvince} AND a.science=#{science}
ORDER BY a.stablityHigh DESC
LIMIT #{limitNum}

SELECT c.* FROM admissionScore a,college c
WHERE c.id=a.collegeId AND a.sprintHigh <= #{ranking} AND a.sprintLow >= #{ranking}
AND c.province=#{localProvince} AND a.science=#{science}
ORDER BY #{ranking}-a.sprintHigh
LIMIT #{limitNum}

SELECT COUNT(c.id) FROM admissionScore a,college c
WHERE c.id=a.collegeId AND a.ensureHigh <= #{ranking} AND a.ensureLow >= #{ranking}
AND c.province=#{localProvince} AND a.science=#{science}

SELECT COUNT(c.id) FROM admissionScore a,college c
WHERE c.id=a.collegeId AND a.stablityHigh <= #{ranking} AND a.stablityLow >= #{ranking}
AND c.province=#{localProvince} AND a.science=#{science}

SELECT COUNT(c.id) FROM admissionScore a,college c
WHERE c.id=a.collegeId AND a.sprintHigh <= #{ranking} AND a.sprintLow >= #{ranking}
AND c.province=#{localProvince} AND a.science=#{science}
